SELECT DISTINCT (timestamp),(body),(attributes),(resource) FROM logs WHERE (timestamp >= toUnixTimestamp64Nano(17175110000000000) AND timestamp <= toUnixTimestamp64Nano(17176110000000000)) AND (JSONExtract(attributes, 'label', 'String') = 'value' OR JSONExtract(resource, 'label', 'String') = 'value') AND (NOT (positionUTF8(body, 'line') > 0)) AND (hex(span_id) IN ('deaddead', 'aaaabbbb')) AND (hex(trace_id) = unhex('deaddead')) LIMIT 1000